        "waf_service": {
            "application": {
                "description": "{{EAP_RECORD}}",
                "fqdn": "{{EAP_RECORD}}",
                "http": {
                    "enabled": true,
                    "port": 80
                },
                "waf_regions": {
                    "aws": {
                        "us-east-1": {
                            "endpoint": {
                                "http": {
                                    "port": 80,
                                    "enabled": true
                                },
                                "ips": [
                                    "18.232.64.254"
                                ]
                            }
                        }
                    }
                }
            },
            "policy": {
                "encoding": "utf-8",
                "threat_campaigns": {
                    "enabled": true,
                    "enforcement_mode": "blocking"
                },
                "compliance_enforcement": {
                    "data_guard": {
                        "enabled": true,
                        "cc": true,
                        "ssn": true
                    },
                    "sensitive_parameters": {
                        "enabled": true,
                        "parameters": [
                            "cc_id",
                            "creditcard",
                            "passwd",
                            "password"
                        ],
                        "xml_attributes": [],
                        "xml_elements": []
                    }
                },
                "high_risk_attack_mitigation": {
                    "enabled": true,
                    "enforcement_mode": "blocking",
                    "signature_enforcement": {
                        "enabled": true
                    },
                    "allowed_methods": {
                        "enabled": true,
                        "methods": [
                            {
                                "name": "GET"
                            },
                            {
                                "name": "POST"
                            },
                            {
                                "name": "HEAD"
                            }
                        ]
                    },
                    "disallowed_file_types": {
                        "enabled": true,
                        "file_types": [
                            "back",
                            "bat",
                            "bck",
                            "bin",
                            "cfg",
                            "cmd",
                            "com",
                            "config",
                            "dat",
                            "dll",
                            "eml",
                            "exe",
                            "exe1",
                            "exe_renamed",
                            "hta",
                            "htr",
                            "htw",
                            "ida",
                            "idc",
                            "idq",
                            "ini",
                            "old",
                            "sav",
                            "save"
                        ]
                    },
                    "api_compliance_enforcement": {
                        "enabled": true
                    },
                    "http_compliance_enforcement": {
                        "enabled": true
                    },
                    "websocket_compliance_enforcement": {
                        "enabled": true
                    },
                    "geolocation_enforcement": {
                        "disallowed_country_codes": [
                            "CU",
                            "IR",
                            "KP",
                            "LY",
                            "SD",
                            "SY"
                        ],
                        "enabled": true
                    },
                    "ip_enforcement": {
                        "enabled": true,
                        "ips": []
                    }
                },
                "malicious_ip_enforcement": {
                    "enabled": true,
                    "enforcement_mode": "blocking",
                    "ip_categories": [
                        {
                            "block": true,
                            "log": true,
                            "name": "mobile_threats"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "cloud_services"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "anonymous_proxies"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "phishing_proxies"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "infected_sources"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "denial_of_service"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "scanners"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "bot_nets"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "web_attacks"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "windows_exploits"
                        },
                        {
                            "block": true,
                            "log": true,
                            "name": "spam_sources"
                        }
                    ]
                }
            }
        },
        "update_comment": "update monitor to block"
    },
    "service_type": "waf"
}

